package com.syntax.homework07;

public class Youtubevid {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// nested loops- loops inside a loop
		
		int outerloop=0;
		int innerloop=0;
		
		for (outerloop=0; outerloop <5; outerloop ++) {
			for (innerloop=0; innerloop <=outerloop; innerloop++) { 
				System.out.println(outerloop);
			}
			
		}
		
		
			
		}
	}


